import React, { useState, useEffect } from 'react';
import { 
  LogOut, Home, Package, ClipboardList, FileCheck, Building,
  AlertCircle
} from 'lucide-react';
import { apiCall } from '../utils/api';
import StatsCard from './StatsCard';

const VendorPortal = ({ user, onLogout }) => {
  const [activeTab, setActiveTab] = useState('dashboard');
  const [ndaStatus, setNdaStatus] = useState('pending');
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    loadVendorData();
  }, []);

  const loadVendorData = async () => {
    try {
      setLoading(true);
      const data = await apiCall('/api/vendor/dashboard-data');
      setNdaStatus(data.registrationStatus || 'pending');
    } catch (error) {
      console.error('Failed to load vendor data:', error);
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center h-screen">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-amber-500"></div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <header className="bg-white shadow-sm border-b">
        <div className="max-w-7xl mx-auto px-6 py-4">
          <div className="flex justify-between items-center">
            <div className="flex items-center gap-4">
              <div className="w-12 h-12 bg-gradient-to-br from-purple-400 to-purple-600 rounded-xl flex items-center justify-center">
                <Building className="text-white" size={24} />
              </div>
              <div>
                <h1 className="text-2xl font-bold text-gray-900">Vendor Portal</h1>
                <p className="text-sm text-gray-500">Welcome, {user.name || user.company}</p>
              </div>
            </div>
            
            <div className="flex items-center gap-3">
              <div className="flex items-center gap-2 px-4 py-2 bg-gray-100 rounded-xl">
                <span className="text-sm font-medium">{user.company || user.name}</span>
              </div>
              <button onClick={onLogout} className="p-2 text-gray-600 hover:bg-gray-100 rounded-xl">
                <LogOut size={20} />
              </button>
            </div>
          </div>
        </div>
      </header>

      {/* Navigation */}
      <nav className="bg-white border-b">
        <div className="max-w-7xl mx-auto px-6">
          <div className="flex gap-6">
            {[
              { id: 'dashboard', label: 'Dashboard', icon: Home },
              { id: 'services', label: 'Services', icon: Package },
              { id: 'orders', label: 'Orders', icon: ClipboardList },
              { id: 'nda', label: 'NDA Status', icon: FileCheck }
            ].map(tab => (
              <button
                key={tab.id}
                onClick={() => setActiveTab(tab.id)}
                className={`flex items-center gap-2 px-4 py-3 border-b-2 transition-colors ${
                  activeTab === tab.id
                    ? 'border-purple-500 text-purple-600'
                    : 'border-transparent text-gray-600 hover:text-gray-900'
                }`}
              >
                <tab.icon size={20} />
                {tab.label}
              </button>
            ))}
          </div>
        </div>
      </nav>

      {/* Content */}
      <main className="max-w-7xl mx-auto px-6 py-8">
        {activeTab === 'dashboard' && (
          <div className="space-y-6">
            <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
              <StatsCard
                icon={FileCheck}
                title="NDA Status"
                value={ndaStatus.toUpperCase()}
                color={ndaStatus === 'approved' ? 'bg-gradient-to-br from-green-500 to-green-600' : 'bg-gradient-to-br from-amber-500 to-amber-600'}
              />
              <StatsCard
                icon={ClipboardList}
                title="Active Orders"
                value={orders.length}
                color="bg-gradient-to-br from-blue-500 to-blue-600"
              />
              <StatsCard
                icon={Package}
                title="Services Offered"
                value="3"
                color="bg-gradient-to-br from-purple-500 to-purple-600"
              />
            </div>

            {ndaStatus !== 'approved' && (
              <div className="bg-amber-50 border border-amber-200 rounded-xl p-6">
                <div className="flex items-start gap-4">
                  <AlertCircle className="text-amber-600 flex-shrink-0" size={24} />
                  <div>
                    <h3 className="font-bold text-amber-900 mb-2">NDA Pending</h3>
                    <p className="text-amber-700 text-sm mb-4">
                      Your NDA is currently under review. You'll receive full portal access once approved.
                    </p>
                    <button className="px-4 py-2 bg-amber-600 text-white rounded-lg hover:bg-amber-700 text-sm font-medium">
                      Check Status
                    </button>
                  </div>
                </div>
              </div>
            )}

            <div className="bg-white rounded-xl shadow-sm border p-6">
              <h2 className="text-xl font-bold mb-4">Recent Activity</h2>
              <div className="space-y-3">
                <div className="flex items-center gap-3 p-4 bg-gray-50 rounded-lg">
                  <FileCheck className="text-green-500" size={20} />
                  <div className="flex-1">
                    <p className="font-medium">NDA Submitted</p>
                    <p className="text-sm text-gray-500">Your NDA form has been submitted for review</p>
                  </div>
                  <span className="text-sm text-gray-500">2 days ago</span>
                </div>
              </div>
            </div>
          </div>
        )}

        {activeTab === 'services' && (
          <div className="space-y-6">
            <div className="flex justify-between items-center">
              <h2 className="text-2xl font-bold">My Services</h2>
              <button className="flex items-center gap-2 px-4 py-2 bg-purple-500 text-white rounded-lg hover:bg-purple-600">
                <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 4v16m8-8H4" />
                </svg>
                Add Service
              </button>
            </div>

            <div className="grid gap-4">
              {[
                { name: 'IT Consulting', status: 'Active', price: '$150/hr' },
                { name: 'Software Development', status: 'Active', price: '$200/hr' },
                { name: 'Cloud Services', status: 'Pending', price: '$180/hr' }
              ].map((service, index) => (
                <div key={index} className="bg-white rounded-xl shadow-sm border p-6">
                  <div className="flex items-center justify-between">
                    <div>
                      <h3 className="text-lg font-bold mb-1">{service.name}</h3>
                      <p className="text-gray-600 text-sm">{service.price}</p>
                    </div>
                    <span className={`px-3 py-1 text-xs font-semibold rounded-full ${
                      service.status === 'Active' ? 'bg-green-100 text-green-700' : 'bg-yellow-100 text-yellow-700'
                    }`}>
                      {service.status}
                    </span>
                  </div>
                </div>
              ))}
            </div>
          </div>
        )}

        {activeTab === 'orders' && (
          <div className="space-y-6">
            <h2 className="text-2xl font-bold">My Orders</h2>
            <div className="bg-white rounded-xl shadow-sm border p-12 text-center">
              <ClipboardList className="mx-auto text-gray-300 mb-4" size={48} />
              <p className="text-gray-500 mb-2">No orders yet</p>
              <p className="text-sm text-gray-400">Orders will appear here once assigned</p>
            </div>
          </div>
        )}

        {activeTab === 'nda' && (
          <div className="space-y-6">
            <h2 className="text-2xl font-bold">NDA Status</h2>
            
            <div className="bg-white rounded-xl shadow-sm border p-6">
              <div className="flex items-center gap-4 mb-6">
                <div className={`w-16 h-16 rounded-full flex items-center justify-center ${
                  ndaStatus === 'approved' ? 'bg-green-100' : 'bg-amber-100'
                }`}>
                  <FileCheck className={ndaStatus === 'approved' ? 'text-green-600' : 'text-amber-600'} size={32} />
                </div>
                <div>
                  <h3 className="text-xl font-bold mb-1">Status: {ndaStatus.toUpperCase()}</h3>
                  <p className="text-gray-600">
                    {ndaStatus === 'approved' 
                      ? 'Your NDA has been approved and you have full portal access'
                      : 'Your NDA is currently under review'}
                  </p>
                </div>
              </div>

              {ndaStatus !== 'approved' && (
                <div className="bg-amber-50 border border-amber-200 rounded-lg p-4">
                  <p className="text-sm text-amber-800">
                    <strong>Next Steps:</strong> Our team is reviewing your submission. 
                    You'll receive an email notification once the review is complete.
                  </p>
                </div>
              )}
            </div>
          </div>
        )}
      </main>
    </div>
  );
};

export default VendorPortal;
