import React, { useState, useEffect } from 'react';
import { 
  Users, FileText, CheckCircle, Bell, LogOut, Home, Briefcase,
  BarChart3, Settings, Send, Download, Plus, TrendingUp
} from 'lucide-react';
import { apiCall } from '../utils/api';
import StatsCard from './StatsCard';
import Notification from './Notification';
import AnalyticsView from './AnalyticsView';
import VendorsView from './VendorsView';
import FormsView from './FormsView';
import NDAFormsView from './NDAFormsView';
import SendNDAModal from './SendNDAModal';

const AdminDashboard = ({ user, onLogout }) => {
  const [activeTab, setActiveTab] = useState('dashboard');
  const [vendors, setVendors] = useState([]);
  const [submittedForms, setSubmittedForms] = useState([]);
  const [stats, setStats] = useState({});
  const [loading, setLoading] = useState(true);
  const [notification, setNotification] = useState(null);
  const [showSendModal, setShowSendModal] = useState(false);
  const [showNotifications, setShowNotifications] = useState(false);
  const [notifications, setNotifications] = useState([]);

  useEffect(() => {
    loadData();
  }, []);

  const loadData = async () => {
    try {
      setLoading(true);
      const [vendorsData, formsData, statsData, notificationsData] = await Promise.all([
        apiCall('/api/admin/vendors'),
        apiCall('/api/admin/submitted-nda-forms'),
        apiCall('/api/dashboard/stats'),
        apiCall('/api/admin/notifications')
      ]);
      
      setVendors(vendorsData);
      setSubmittedForms(formsData);
      setNotifications(notificationsData.notifications || []);
      
      const metadata = vendorsData.find(v => v.id === 'metadata');
      setStats({
        totalVendors: vendorsData.filter(v => v.id !== 'metadata').length,
        ndaCompleted: metadata?.nda_completed_count || 0,
        ndaSent: metadata?.nda_sent_count || 0,
        portalAccess: metadata?.portal_access_count || 0,
        ...statsData
      });
    } catch (error) {
      showNotification('Failed to load data', 'error');
    } finally {
      setLoading(false);
    }
  };

  const showNotification = (message, type = 'info') => {
    setNotification({ message, type });
  };

  const handleSendNDA = async (vendor) => {
    try {
      await apiCall('/api/admin/send-nda', {
        method: 'POST',
        body: JSON.stringify(vendor)
      });
      showNotification(`NDA sent to ${vendor.company_name || vendor.email}!`, 'success');
      loadData();
    } catch (error) {
      showNotification('Failed to send NDA', 'error');
    }
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center h-screen">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-amber-500"></div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      {notification && (
        <Notification
          message={notification.message}
          type={notification.type}
          onClose={() => setNotification(null)}
        />
      )}

      {/* Header */}
      <header className="bg-white shadow-sm border-b">
        <div className="max-w-7xl mx-auto px-6 py-4">
          <div className="flex justify-between items-center">
            <div className="flex items-center gap-4">
              <div className="w-12 h-12 bg-gradient-to-br from-amber-400 to-amber-600 rounded-xl flex items-center justify-center">
                <span className="text-white font-bold text-xl">Y</span>
              </div>
              <div>
                <h1 className="text-2xl font-bold text-gray-900">YellowStone XPs</h1>
                <p className="text-sm text-gray-500">Admin Dashboard</p>
              </div>
            </div>
            
            <div className="flex items-center gap-3">
              <div className="relative">
                <button 
                  onClick={() => setShowNotifications(!showNotifications)}
                  className="relative p-2 text-gray-600 hover:bg-gray-100 rounded-xl"
                >
                  <Bell size={20} />
                  {notifications.length > 0 && (
                    <span className="absolute top-1 right-1 w-5 h-5 bg-red-500 rounded-full text-white text-xs flex items-center justify-center">
                      {notifications.length}
                    </span>
                  )}
                </button>
                
                {showNotifications && (
                  <div className="absolute right-0 top-12 w-80 bg-white rounded-xl shadow-lg border z-50">
                    <div className="p-4 border-b">
                      <h3 className="font-semibold text-gray-900">Notifications</h3>
                    </div>
                    <div className="max-h-96 overflow-y-auto">
                      {notifications.length === 0 ? (
                        <div className="p-4 text-center text-gray-500">
                          No notifications
                        </div>
                      ) : (
                        notifications.map((notif, index) => (
                          <div key={index} className="p-4 border-b hover:bg-gray-50">
                            <div className="flex items-start gap-3">
                              <div className="w-2 h-2 bg-blue-500 rounded-full mt-2"></div>
                              <div className="flex-1">
                                <p className="text-sm font-medium text-gray-900">{notif.title}</p>
                                <p className="text-xs text-gray-500 mt-1">{notif.message}</p>
                                <p className="text-xs text-gray-400 mt-1">{new Date(notif.created_at).toLocaleString()}</p>
                              </div>
                            </div>
                          </div>
                        ))
                      )}
                    </div>
                    {notifications.length > 0 && (
                      <div className="p-3 border-t">
                        <button 
                          onClick={() => {
                            setNotifications([]);
                            setShowNotifications(false);
                          }}
                          className="w-full text-sm text-blue-600 hover:text-blue-800"
                        >
                          Mark all as read
                        </button>
                      </div>
                    )}
                  </div>
                )}
              </div>
              <button className="p-2 text-gray-600 hover:bg-gray-100 rounded-xl">
                <Settings size={20} />
              </button>
              <div className="flex items-center gap-2 px-4 py-2 bg-gray-100 rounded-xl">
                <div className="w-8 h-8 bg-gradient-to-br from-purple-400 to-purple-600 rounded-lg flex items-center justify-center">
                  <span className="text-white text-sm font-bold">A</span>
                </div>
                <span className="text-sm font-medium">{user.name || 'Admin'}</span>
              </div>
              <button onClick={onLogout} className="p-2 text-gray-600 hover:bg-gray-100 rounded-xl">
                <LogOut size={20} />
              </button>
            </div>
          </div>
        </div>
      </header>

      {/* Navigation */}
      <nav className="bg-white border-b">
        <div className="max-w-7xl mx-auto px-6">
          <div className="flex gap-6">
            {[
              { id: 'dashboard', label: 'Dashboard', icon: Home },
              { id: 'analytics', label: 'Analytics', icon: BarChart3 },
              { id: 'vendors', label: 'Vendors', icon: Users },
              { id: 'forms', label: 'Forms', icon: FileText },
              { id: 'nda-forms', label: 'NDA Forms', icon: FileText }
            ].map(tab => (
              <button
                key={tab.id}
                onClick={() => setActiveTab(tab.id)}
                className={`flex items-center gap-2 px-4 py-3 border-b-2 transition-colors ${
                  activeTab === tab.id
                    ? 'border-amber-500 text-amber-600'
                    : 'border-transparent text-gray-600 hover:text-gray-900'
                }`}
              >
                <tab.icon size={20} />
                {tab.label}
              </button>
            ))}
          </div>
        </div>
      </nav>

      {/* Content */}
      <main className="max-w-7xl mx-auto px-6 py-8">
        {activeTab === 'dashboard' && (
          <div className="space-y-6">
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
              <StatsCard
                icon={Users}
                title="Total Vendors"
                value={stats.totalVendors}
                color="bg-gradient-to-br from-blue-500 to-blue-600"
                trend="+12%"
              />
              <StatsCard
                icon={CheckCircle}
                title="NDA Completed"
                value={stats.ndaCompleted}
                color="bg-gradient-to-br from-green-500 to-green-600"
                trend="+8%"
              />
              <StatsCard
                icon={FileText}
                title="Total Tasks"
                value={stats.total_tasks || 0}
                color="bg-gradient-to-br from-purple-500 to-purple-600"
              />
              <StatsCard
                icon={Briefcase}
                title="Projects"
                value={stats.total_projects || 0}
                color="bg-gradient-to-br from-amber-500 to-amber-600"
              />
            </div>

            {/* Quick Actions */}
            <div className="bg-white rounded-xl shadow-sm p-6 border">
              <h2 className="text-xl font-bold mb-4">Quick Actions</h2>
              <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                <button
                  onClick={() => setShowSendModal(true)}
                  className="flex flex-col items-center gap-3 p-6 bg-gradient-to-br from-amber-500 to-amber-600 text-white rounded-xl hover:shadow-lg transition-all"
                >
                  <Send size={24} />
                  <span className="font-semibold">Send NDA</span>
                </button>
                <button className="flex flex-col items-center gap-3 p-6 bg-gradient-to-br from-blue-500 to-blue-600 text-white rounded-xl hover:shadow-lg transition-all">
                  <Download size={24} />
                  <span className="font-semibold">Export Data</span>
                </button>
                <button className="flex flex-col items-center gap-3 p-6 bg-gradient-to-br from-green-500 to-green-600 text-white rounded-xl hover:shadow-lg transition-all">
                  <BarChart3 size={24} />
                  <span className="font-semibold">View Reports</span>
                </button>
              </div>
            </div>
          </div>
        )}

        {activeTab === 'analytics' && <AnalyticsView stats={stats} vendors={vendors} />}
        {activeTab === 'vendors' && <VendorsView vendors={vendors} onSendNDA={handleSendNDA} onReload={loadData} showNotification={showNotification} />}
        {activeTab === 'forms' && <FormsView forms={submittedForms} showNotification={showNotification} />}
        {activeTab === 'nda-forms' && <NDAFormsView showNotification={showNotification} />}
      </main>

      {showSendModal && (
        <SendNDAModal
          onClose={() => setShowSendModal(false)}
          onSend={(data) => {
            handleSendNDA(data);
            setShowSendModal(false);
          }}
        />
      )}
    </div>
  );
};

export default AdminDashboard;
