                  <input
                    type="tel"
                    value={profileForm.phone}
                    onChange={(e) => setProfileForm({...profileForm, phone: e.target.value})}
                    className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                    placeholder="Enter phone number"
                  />
                    </div>
                
                    <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">Emergency Contact</label>
                  <input
                    type="text"
                    value={profileForm.emergencyContact}
                    onChange={(e) => setProfileForm({...profileForm, emergencyContact: e.target.value})}
                    className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                    placeholder="Emergency contact name and number"
                  />
                    </div>
              </div>

              {/* Address */}
                    <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">Address</label>
                <textarea
                  value={profileForm.address}
                  onChange={(e) => setProfileForm({...profileForm, address: e.target.value})}
                  rows={3}
                  className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                  placeholder="Enter your address"
                />
                    </div>

              {/* Skills */}
                    <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">Skills</label>
                <input
                  type="text"
                  value={profileForm.skills}
                  onChange={(e) => setProfileForm({...profileForm, skills: e.target.value})}
                  className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                  placeholder="e.g., JavaScript, Python, Project Management"
                />
                    </div>

              {/* Bio */}
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">Bio</label>
                <textarea
                  value={profileForm.bio}
                  onChange={(e) => setProfileForm({...profileForm, bio: e.target.value})}
                  rows={4}
                  className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                  placeholder="Tell us about yourself..."
                />
                  </div>
                </div>
            <div className="p-6 border-t flex justify-end gap-3">
              <button
                onClick={() => setShowProfileModal(false)}
                className="px-4 py-2 text-gray-600 hover:text-gray-800"
              >
                Cancel
              </button>
              <button
                onClick={() => {
                  setShowProfileModal(false);
                  // Handle profile save
                }}
                className="px-4 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600"
              >
                Save Changes
              </button>
              </div>
            </div>
          </div>
        )}

        {/* Report Viewer Modal */}
        {showReportViewer && selectedReport && (
          <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
            <div className="bg-white rounded-xl shadow-xl max-w-6xl w-full max-h-[90vh] flex flex-col">
              {/* Header */}
              <div className="flex items-center justify-between p-6 border-b">
                <div>
                  <h3 className="text-xl font-semibold text-gray-900">{selectedReport.name}</h3>
                  <p className="text-sm text-gray-600">{selectedReport.type} â€¢ {selectedReport.uploadDate}</p>
                </div>
                <div className="flex items-center gap-2">
                  <button
                    onClick={() => handleDownloadReport(selectedReport)}
                    className="px-3 py-1 text-sm bg-blue-500 text-white rounded-lg hover:bg-blue-600 flex items-center gap-1"
                  >
                    <Download className="h-4 w-4" />
                    Download
                  </button>
                  <button
                    onClick={closeReportViewer}
                    className="p-2 text-gray-400 hover:text-gray-600 hover:bg-gray-100 rounded-lg"
                  >
                    <X className="h-5 w-5" />
                  </button>
                </div>
              </div>

              {/* Content */}
              <div className="flex-1 overflow-auto p-6">
                {reportContent?.type === 'csv' && (
                  <div className="space-y-4">
                    <div className="flex items-center justify-between">
                      <h4 className="text-lg font-medium text-gray-900">CSV Data Preview</h4>
                      <div className="text-sm text-gray-500">
                        {reportContent.data.rows.length} rows â€¢ {reportContent.data.headers.length} columns
                      </div>
                    </div>
                    
                    {reportContent.data.headers.length === 0 ? (
                      <div className="bg-red-50 border border-red-200 rounded-lg p-6 text-center">
                        <AlertCircle className="h-12 w-12 text-red-600 mx-auto mb-4" />
                        <p className="text-red-700">No data found in CSV file or file format is invalid.</p>
                      </div>
                    ) : (
                      <div className="overflow-x-auto border border-gray-200 rounded-lg">
                        <table className="min-w-full">
                          <thead className="bg-gray-50">
                            <tr>
                              {reportContent.data.headers.map((header, index) => (
                                <th key={index} className="px-4 py-3 text-left text-sm font-medium text-gray-700 border-b border-gray-200">
                                  {header || `Column ${index + 1}`}
                                </th>
                              ))}
                            </tr>
                          </thead>
                          <tbody className="bg-white divide-y divide-gray-200">
                            {reportContent.data.rows.slice(0, 100).map((row, rowIndex) => (
                              <tr key={rowIndex} className="hover:bg-gray-50">
                                {reportContent.data.headers.map((_, cellIndex) => (
                                  <td key={cellIndex} className="px-4 py-3 text-sm text-gray-900">
                                    {row[cellIndex] || '-'}
                                  </td>
                                ))}
                              </tr>
                            ))}
                          </tbody>
                        </table>
                      </div>
                    )}
                    
                    {reportContent.data.rows.length > 100 && (
                      <div className="bg-blue-50 border border-blue-200 rounded-lg p-4 text-center">
                        <p className="text-sm text-blue-700">
                          Showing first 100 rows of {reportContent.data.rows.length} total rows.
                          <br />
                          Download the file to view all data.
                        </p>
                      </div>
                    )}
                  </div>
                )}

                {reportContent?.type === 'pdf' && (
                  <div className="space-y-4">
                    <h4 className="text-lg font-medium text-gray-900">PDF Document</h4>
                    <div className="border border-gray-200 rounded-lg overflow-hidden">
                      <iframe
                        src={reportContent.url}
                        className="w-full h-[600px]"
                        title="PDF Viewer"
                      />
                    </div>
                  </div>
                )}

                {reportContent?.type === 'excel' && (
                  <div className="space-y-4">
                    <h4 className="text-lg font-medium text-gray-900">Excel File</h4>
                    <div className="bg-yellow-50 border border-yellow-200 rounded-lg p-6 text-center">
                      <FileText className="h-12 w-12 text-yellow-600 mx-auto mb-4" />
                      <p className="text-gray-700 mb-4">{reportContent.message}</p>
                      <button
                        onClick={() => handleDownloadReport(selectedReport)}
                        className="px-4 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600 flex items-center gap-2 mx-auto"
                      >
                        <Download className="h-4 w-4" />
                        Download Excel File
                      </button>
                    </div>
                  </div>
                )}
              </div>
            </div>
          </div>
        )}
    </div>
  );
};

export default EmployeeDashboard;
