import React, { useState, useEffect } from 'react';
import { 
  Bell, LogOut, Home, CheckSquare, FolderKanban, User,
  Calendar, ArrowRight
} from 'lucide-react';
import { apiCall } from '../utils/api';
import StatsCard from './StatsCard';

const EmployeeDashboard = ({ user, onLogout }) => {
  const [activeTab, setActiveTab] = useState('dashboard');
  const [tasks, setTasks] = useState([]);
  const [workflows, setWorkflows] = useState([]);
  const [notifications, setNotifications] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    loadEmployeeData();
  }, []);

  const loadEmployeeData = async () => {
    try {
      setLoading(true);
      const [tasksData, workflowsData, notificationsData] = await Promise.all([
        apiCall('/api/tasks'),
        apiCall('/api/employee/workflows'),
        apiCall('/api/employee/notifications')
      ]);
      
      setTasks(tasksData);
      setWorkflows(workflowsData.workflows || []);
      setNotifications(notificationsData.notifications || []);
    } catch (error) {
      console.error('Failed to load employee data:', error);
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center h-screen">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-amber-500"></div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <header className="bg-white shadow-sm border-b">
        <div className="max-w-7xl mx-auto px-6 py-4">
          <div className="flex justify-between items-center">
            <div className="flex items-center gap-4">
              <div className="w-12 h-12 bg-gradient-to-br from-blue-400 to-blue-600 rounded-xl flex items-center justify-center">
                <User className="text-white" size={24} />
              </div>
              <div>
                <h1 className="text-2xl font-bold text-gray-900">Employee Portal</h1>
                <p className="text-sm text-gray-500">Welcome, {user.name}</p>
              </div>
            </div>
            
            <div className="flex items-center gap-3">
              <button className="relative p-2 text-gray-600 hover:bg-gray-100 rounded-xl">
                <Bell size={20} />
                {notifications.length > 0 && (
                  <span className="absolute top-1 right-1 w-5 h-5 bg-red-500 rounded-full text-white text-xs flex items-center justify-center">
                    {notifications.length}
                  </span>
                )}
              </button>
              <div className="flex items-center gap-2 px-4 py-2 bg-gray-100 rounded-xl">
                <span className="text-sm font-medium">{user.name}</span>
              </div>
              <button onClick={onLogout} className="p-2 text-gray-600 hover:bg-gray-100 rounded-xl">
                <LogOut size={20} />
              </button>
            </div>
          </div>
        </div>
      </header>

      {/* Navigation */}
      <nav className="bg-white border-b">
        <div className="max-w-7xl mx-auto px-6">
          <div className="flex gap-6">
            {[
              { id: 'dashboard', label: 'Dashboard', icon: Home },
              { id: 'tasks', label: 'My Tasks', icon: CheckSquare },
              { id: 'workflows', label: 'Workflows', icon: FolderKanban },
              { id: 'profile', label: 'Profile', icon: User }
            ].map(tab => (
              <button
                key={tab.id}
                onClick={() => setActiveTab(tab.id)}
                className={`flex items-center gap-2 px-4 py-3 border-b-2 transition-colors ${
                  activeTab === tab.id
                    ? 'border-blue-500 text-blue-600'
                    : 'border-transparent text-gray-600 hover:text-gray-900'
                }`}
              >
                <tab.icon size={20} />
                {tab.label}
              </button>
            ))}
          </div>
        </div>
      </nav>

      {/* Content */}
      <main className="max-w-7xl mx-auto px-6 py-8">
        {activeTab === 'dashboard' && (
          <div className="space-y-6">
            {/* Stats */}
            <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
              <StatsCard
                icon={CheckSquare}
                title="My Tasks"
                value={tasks.length}
                color="bg-gradient-to-br from-blue-500 to-blue-600"
                subtitle="Assigned to you"
              />
              <StatsCard
                icon={FolderKanban}
                title="Active Workflows"
                value={workflows.length}
                color="bg-gradient-to-br from-purple-500 to-purple-600"
                subtitle="In progress"
              />
              <StatsCard
                icon={Bell}
                title="Notifications"
                value={notifications.length}
                color="bg-gradient-to-br from-amber-500 to-amber-600"
                subtitle="Unread messages"
              />
            </div>

            {/* Recent Tasks */}
            <div className="bg-white rounded-xl shadow-sm border p-6">
              <h2 className="text-xl font-bold mb-4">Recent Tasks</h2>
              <div className="space-y-3">
                {tasks.slice(0, 5).map(task => (
                  <div key={task._id} className="flex items-center justify-between p-4 bg-gray-50 rounded-lg hover:bg-gray-100 transition-colors">
                    <div className="flex items-center gap-3">
                      <div className={`w-2 h-2 rounded-full ${
                        task.priority === 'High' ? 'bg-red-500' :
                        task.priority === 'Medium' ? 'bg-yellow-500' :
                        'bg-green-500'
                      }`}></div>
                      <div>
                        <p className="font-medium">{task.title}</p>
                        <p className="text-sm text-gray-500">{task.description}</p>
                      </div>
                    </div>
                    <span className={`px-3 py-1 text-xs font-semibold rounded-full ${
                      task.status === 'Completed' ? 'bg-green-100 text-green-700' :
                      task.status === 'In Progress' ? 'bg-blue-100 text-blue-700' :
                      'bg-gray-100 text-gray-700'
                    }`}>
                      {task.status}
                    </span>
                  </div>
                ))}
                {tasks.length === 0 && (
                  <p className="text-center text-gray-500 py-8">No tasks assigned yet</p>
                )}
              </div>
            </div>
          </div>
        )}

        {activeTab === 'tasks' && (
          <div className="space-y-6">
            <h2 className="text-2xl font-bold">My Tasks</h2>
            <div className="grid gap-4">
              {tasks.map(task => (
                <div key={task._id} className="bg-white rounded-xl shadow-sm border p-6">
                  <div className="flex items-start justify-between mb-4">
                    <div className="flex-1">
                      <div className="flex items-center gap-3 mb-2">
                        <h3 className="text-lg font-bold">{task.title}</h3>
                        <span className={`px-2 py-1 text-xs font-semibold rounded-full ${
                          task.priority === 'High' ? 'bg-red-100 text-red-700' :
                          task.priority === 'Medium' ? 'bg-yellow-100 text-yellow-700' :
                          'bg-green-100 text-green-700'
                        }`}>
                          {task.priority}
                        </span>
                      </div>
                      <p className="text-gray-600 text-sm mb-3">{task.description}</p>
                      <div className="flex items-center gap-4 text-sm text-gray-500">
                        <span className="flex items-center gap-1">
                          <Calendar size={14} />
                          Due: {task.due_date || 'No deadline'}
                        </span>
                        <span className="flex items-center gap-1">
                          <User size={14} />
                          Assigned by: {task.assigned_by}
                        </span>
                      </div>
                    </div>
                    <select
                      value={task.status}
                      onChange={(e) => {
                        // Handle status update
                        console.log('Update task status:', task._id, e.target.value);
                      }}
                      className="px-3 py-2 border rounded-lg text-sm focus:outline-none focus:ring-2 focus:ring-blue-500"
                    >
                      <option value="Pending">Pending</option>
                      <option value="In Progress">In Progress</option>
                      <option value="Completed">Completed</option>
                    </select>
                  </div>
                </div>
              ))}
              {tasks.length === 0 && (
                <div className="bg-white rounded-xl shadow-sm border p-12 text-center">
                  <CheckSquare className="mx-auto text-gray-300 mb-4" size={48} />
                  <p className="text-gray-500">No tasks found</p>
                </div>
              )}
            </div>
          </div>
        )}

        {activeTab === 'workflows' && (
          <div className="space-y-6">
            <div className="flex justify-between items-center">
              <h2 className="text-2xl font-bold">My Workflows</h2>
              <span className="text-sm text-gray-500">{workflows.length} workflows</span>
            </div>

            <div className="grid gap-4">
              {workflows.map(workflow => (
                <div key={workflow._id} className="bg-white rounded-xl shadow-sm border p-6">
                  <div className="flex items-start justify-between">
                    <div className="flex-1">
                      <h3 className="text-lg font-bold mb-2">{workflow.name}</h3>
                      <p className="text-gray-600 text-sm mb-3">{workflow.description}</p>
                      <div className="flex items-center gap-2">
                        <span className={`px-3 py-1 text-xs font-semibold rounded-full ${
                          workflow.status === 'Active' ? 'bg-green-100 text-green-700' :
                          'bg-gray-100 text-gray-700'
                        }`}>
                          {workflow.status}
                        </span>
                        <span className={`px-3 py-1 text-xs font-semibold rounded-full ${
                          workflow.priority === 'High' ? 'bg-red-100 text-red-700' :
                          workflow.priority === 'Medium' ? 'bg-yellow-100 text-yellow-700' :
                          'bg-green-100 text-green-700'
                        }`}>
                          {workflow.priority}
                        </span>
                      </div>
                    </div>
                    <button className="px-4 py-2 text-blue-600 hover:bg-blue-50 rounded-lg flex items-center gap-2">
                      View Details
                      <ArrowRight size={16} />
                    </button>
                  </div>
                </div>
              ))}
              {workflows.length === 0 && (
                <div className="bg-white rounded-xl shadow-sm border p-12 text-center">
                  <FolderKanban className="mx-auto text-gray-300 mb-4" size={48} />
                  <p className="text-gray-500">No workflows assigned yet</p>
                </div>
              )}
            </div>
          </div>
        )}

        {activeTab === 'profile' && (
          <div className="space-y-6">
            <h2 className="text-2xl font-bold">My Profile</h2>

            <div className="bg-white rounded-xl shadow-sm border p-6">
              <div className="flex items-start gap-6">
                <div className="w-24 h-24 bg-gradient-to-br from-blue-400 to-blue-600 rounded-xl flex items-center justify-center">
                  <span className="text-white text-3xl font-bold">{user.name?.[0]}</span>
                </div>
                <div className="flex-1">
                  <h3 className="text-xl font-bold mb-1">{user.name}</h3>
                  <p className="text-gray-600 mb-4">{user.designation}</p>
                  <div className="grid grid-cols-2 gap-4 text-sm">
                    <div>
                      <p className="text-gray-500">Employee ID</p>
                      <p className="font-medium">{user.employee_id}</p>
                    </div>
                    <div>
                      <p className="text-gray-500">Department</p>
                      <p className="font-medium">{user.department}</p>
                    </div>
                    <div>
                      <p className="text-gray-500">Email</p>
                      <p className="font-medium">{user.email}</p>
                    </div>
                    <div>
                      <p className="text-gray-500">Manager</p>
                      <p className="font-medium">{user.manager || 'N/A'}</p>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        )}
      </main>
    </div>
  );
};

export default EmployeeDashboard;
