                  <Clock className="h-12 w-12 text-white" />
                </div>
                <h3 className="text-xl font-semibold mb-2">Today's Attendance</h3>
                <p className="text-gray-600 mb-6">Working Hours: 9:30 AM - 6:30 PM</p>
                
