sourcesContent:
import React, { useState, useEffect } from 'react';
import { 
  Users, Plus, Edit, Trash2, Search, Filter, Save, X, 
  UserPlus, Building, Mail, Phone, MapPin, Calendar,
  ChevronDown, ChevronRight, Crown, Shield, User, 
  MoreHorizontal, Eye, Settings, ArrowUpDown
} from 'lucide-react';
import { apiCall } from '../utils/api';

const OrganizationStructureView = ({ showNotification }) => {
  const [loading, setLoading] = useState(false);
  const [employees, setEmployees] = useState([]);
  const [departments, setDepartments] = useState([]);
  const [showAddEmployee, setShowAddEmployee] = useState(false);
  const [showAddDepartment, setShowAddDepartment] = useState(false);
  const [editingEmployee, setEditingEmployee] = useState(null);
  const [editingDepartment, setEditingDepartment] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [departmentFilter, setDepartmentFilter] = useState('all');
  const [expandedNodes, setExpandedNodes] = useState(new Set());
  const [viewMode, setViewMode] = useState('tree'); // tree, list, grid

  // Form states
  const [employeeForm, setEmployeeForm] = useState({
    name: '',
    email: '',
    phone: '',
    position: '',
    department: '',
    manager: '',
    employeeId: '',
    hireDate: '',
    salary: '',
    address: '',
    emergencyContact: '',
    skills: [],
    status: 'active'
  });

  const [departmentForm, setDepartmentForm] = useState({
    name: '',
    description: '',
    head: '',
    budget: '',
    location: '',
    establishedDate: ''
  });

  useEffect(() => {
    loadOrganizationData();
  }, []);

  const loadOrganizationData = async () => {
    try {
      setLoading(true);
      // Mock data - in real app, this would fetch from API
      const mockEmployees = [
        {
          id: 1,
          name: 'John Smith',
          email: 'john.smith@yellowstonexps.com',
          phone: '+91-98765-43210',
          position: 'CEO',
          department: 'Executive',
          manager: null,
          employeeId: 'EMP001',
          hireDate: '2020-01-15',
          salary: '₹15,00,000',
          address: 'Mumbai, Maharashtra',
          emergencyContact: 'Jane Smith - +91-98765-43211',
          skills: ['Leadership', 'Strategy', 'Management'],
          status: 'active',
          avatar: 'JS'
        },
        {
          id: 2,
          name: 'Sarah Johnson',
          email: 'sarah.johnson@yellowstonexps.com',
          phone: '+91-98765-43212',
          position: 'CTO',
          department: 'Technology',
          manager: 1,
          employeeId: 'EMP002',
          hireDate: '2020-03-20',
          salary: '₹12,00,000',
          address: 'Bangalore, Karnataka',
          emergencyContact: 'Mike Johnson - +91-98765-43213',
          skills: ['Technology', 'Innovation', 'Team Management'],
          status: 'active',
          avatar: 'SJ'
        },
        {
          id: 3,
          name: 'Michael Brown',
          email: 'michael.brown@yellowstonexps.com',
          phone: '+91-98765-43214',
          position: 'CFO',
          department: 'Finance',
          manager: 1,
          employeeId: 'EMP003',
          hireDate: '2020-02-10',
          salary: '₹11,00,000',
          address: 'Delhi, NCR',
          emergencyContact: 'Lisa Brown - +91-98765-43215',
          skills: ['Finance', 'Accounting', 'Budgeting'],
          status: 'active',
          avatar: 'MB'
        },
        {
          id: 4,
          name: 'Emily Davis',
          email: 'emily.davis@yellowstonexps.com',
          phone: '+91-98765-43216',
          position: 'Senior Developer',
          department: 'Technology',
          manager: 2,
          employeeId: 'EMP004',
          hireDate: '2021-06-15',
          salary: '₹8,00,000',
          address: 'Pune, Maharashtra',
          emergencyContact: 'David Davis - +91-98765-43217',
          skills: ['React', 'Node.js', 'Python'],
          status: 'active',
          avatar: 'ED'
        },
        {
          id: 5,
          name: 'Robert Wilson',
          email: 'robert.wilson@yellowstonexps.com',
          phone: '+91-98765-43218',
          position: 'HR Manager',
          department: 'Human Resources',
          manager: 1,
          employeeId: 'EMP005',
          hireDate: '2020-08-01',
          salary: '₹9,00,000',
          address: 'Chennai, Tamil Nadu',
          emergencyContact: 'Mary Wilson - +91-98765-43219',
          skills: ['HR Management', 'Recruitment', 'Training'],
          status: 'active',
          avatar: 'RW'
        }
      ];

      const mockDepartments = [
        {
          id: 1,
          name: 'Executive',
          description: 'Executive leadership team',
          head: 1,
          budget: '₹50,00,000',
          location: 'Mumbai',
          establishedDate: '2020-01-01',
          employeeCount: 1
        },
        {
          id: 2,
          name: 'Technology',
          description: 'Software development and IT operations',
          head: 2,
          budget: '₹30,00,000',
          location: 'Bangalore',
          establishedDate: '2020-02-01',
          employeeCount: 2
        },
        {
          id: 3,
          name: 'Finance',
          description: 'Financial planning and accounting',
          head: 3,
          budget: '₹20,00,000',
          location: 'Delhi',
          establishedDate: '2020-02-01',
          employeeCount: 1
        },
        {
          id: 4,
          name: 'Human Resources',
          description: 'Human resources and talent management',
          head: 5,
          budget: '₹15,00,000',
          location: 'Chennai',
          establishedDate: '2020-08-01',
          employeeCount: 1
        }
      ];

      setEmployees(mockEmployees);
      setDepartments(mockDepartments);
    } catch (error) {
      console.error('Error loading organization data:', error);
      showNotification('Failed to load organization data', 'error');
    } finally {
      setLoading(false);
    }
  };

  const buildHierarchy = () => {
    const employeeMap = new Map();
    const rootEmployees = [];

    // Create map of employees
    employees.forEach(emp => {
      employeeMap.set(emp.id, { ...emp, children: [] });
    });

    // Build hierarchy
    employees.forEach(emp => {
      if (emp.manager) {
        const manager = employeeMap.get(emp.manager);
        if (manager) {
          manager.children.push(employeeMap.get(emp.id));
        }
      } else {
        rootEmployees.push(employeeMap.get(emp.id));
      }
    });

    return rootEmployees;
  };

  const filteredEmployees = employees.filter(emp => {
    const matchesSearch = 
      emp.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
      emp.email.toLowerCase().includes(searchTerm.toLowerCase()) ||
      emp.position.toLowerCase().includes(searchTerm.toLowerCase()) ||
      emp.employeeId.toLowerCase().includes(searchTerm.toLowerCase());
    
    const matchesDepartment = departmentFilter === 'all' || emp.department === departmentFilter;
    
    return matchesSearch && matchesDepartment;
  });

  const toggleNode = (nodeId) => {
    const newExpanded = new Set(expandedNodes);
    if (newExpanded.has(nodeId)) {
      newExpanded.delete(nodeId);
    } else {
      newExpanded.add(nodeId);
    }
    setExpandedNodes(newExpanded);
  };

  const handleAddEmployee = async () => {
    try {
      setLoading(true);
      // In real app, this would call API
      const newEmployee = {
        id: employees.length + 1,
        ...employeeForm,
        avatar: employeeForm.name.split(' ').map(n => n[0]).join('').toUpperCase()
      };
      
      setEmployees(prev => [...prev, newEmployee]);
      setShowAddEmployee(false);
      resetEmployeeForm();
      showNotification('Employee added successfully', 'success');
    } catch (error) {
      showNotification('Failed to add employee', 'error');
    } finally {
      setLoading(false);
    }
  };

  const handleEditEmployee = async () => {
    try {
      setLoading(true);
      // In real app, this would call API
      setEmployees(prev => prev.map(emp => 
        emp.id === editingEmployee.id ? { ...emp, ...employeeForm } : emp
      ));
      setEditingEmployee(null);
      resetEmployeeForm();
      showNotification('Employee updated successfully', 'success');
    } catch (error) {
      showNotification('Failed to update employee', 'error');
    } finally {
      setLoading(false);
    }
  };

  const handleDeleteEmployee = async (employeeId) => {
    if (!window.confirm('Are you sure you want to delete this employee?')) return;
    
    try {
      setLoading(true);
      // In real app, this would call API
      setEmployees(prev => prev.filter(emp => emp.id !== employeeId));
      showNotification('Employee deleted successfully', 'success');
    } catch (error) {
      showNotification('Failed to delete employee', 'error');
    } finally {
      setLoading(false);
    }
  };

  const handleAddDepartment = async () => {
    try {
      setLoading(true);
      // In real app, this would call API
      const newDepartment = {
        id: departments.length + 1,
        ...departmentForm,
        employeeCount: 0
      };
      
      setDepartments(prev => [...prev, newDepartment]);
      setShowAddDepartment(false);
      resetDepartmentForm();
      showNotification('Department added successfully', 'success');
    } catch (error) {
      showNotification('Failed to add department', 'error');
    } finally {
      setLoading(false);
    }
  };

  const resetEmployeeForm = () => {
    setEmployeeForm({
      name: '',
      email: '',
      phone: '',
      position: '',
      department: '',
      manager: '',
      employeeId: '',
      hireDate: '',
      salary: '',
      address: '',
      emergencyContact: '',
      skills: [],
      status: 'active'
    });
  };

  const resetDepartmentForm = () => {
    setDepartmentForm({
      name: '',
      description: '',
      head: '',
      budget: '',
      location: '',
      establishedDate: ''
    });
  };

  const openEditEmployee = (employee) => {
    setEmployeeForm(employee);
    setEditingEmployee(employee);
  };

  const openEditDepartment = (department) => {
    setDepartmentForm(department);
    setEditingDepartment(department);
  };

  const renderTreeNode = (node, level = 0) => {
    const isExpanded = expandedNodes.has(node.id);
    const hasChildren = node.children && node.children.length > 0;

    return (
      <div key={node.id} className="ml-4">
        <div className="flex items-center gap-3 p-3 hover:bg-gray-50 rounded-lg">
          <button
            onClick={() => hasChildren && toggleNode(node.id)}
            className="w-6 h-6 flex items-center justify-center"
          >
            {hasChildren ? (
              isExpanded ? <ChevronDown size={16} /> : <ChevronRight size={16} />
            ) : (
              <div className="w-4 h-4" />
            )}
          </button>

          <div className="w-10 h-10 bg-gradient-to-br from-blue-400 to-blue-600 rounded-lg flex items-center justify-center">
            <span className="text-white font-semibold text-sm">{node.avatar}</span>
          </div>

          <div className="flex-1">
            <div className="flex items-center gap-2">
              <h3 className="font-medium text-gray-900">{node.name}</h3>
              {node.position === 'CEO' && <Crown size={16} className="text-yellow-500" />}
              {node.position.includes('Manager') && <Shield size={16} className="text-blue-500" />}
            </div>
            <p className="text-sm text-gray-600">{node.position} • {node.department}</p>
            <p className="text-xs text-gray-500">{node.email}</p>
          </div>

          <div className="flex items-center gap-2">
            <span className="text-sm text-gray-600">{node.employeeId}</span>
            <div className="flex gap-1">
              <button
                onClick={() => openEditEmployee(node)}
                className="p-1 text-blue-600 hover:bg-blue-50 rounded"
                title="Edit"
              >
                <Edit size={14} />
              </button>
              <button
                onClick={() => handleDeleteEmployee(node.id)}
                className="p-1 text-red-600 hover:bg-red-50 rounded"
                title="Delete"
              >
                <Trash2 size={14} />
              </button>
            </div>
          </div>
        </div>

        {isExpanded && hasChildren && (
          <div className="ml-6">
            {node.children.map(child => renderTreeNode(child, level + 1))}
          </div>
        )}
      </div>
    );
  };

  if (loading) {
    return (
      <div className="flex justify-center items-center h-64">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-amber-500"></div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex justify-between items-center">
        <div>
          <h2 className="text-2xl font-bold text-gray-900">Organization Structure</h2>
          <p className="text-gray-600 mt-1">Manage employees and departments</p>
        </div>
        <div className="flex gap-3">
          <button
            onClick={() => setShowAddDepartment(true)}
            className="flex items-center gap-2 px-4 py-2 text-gray-600 hover:bg-gray-100 rounded-lg transition-colors"
          >
            <Building size={16} />
            Add Department
          </button>
          <button
            onClick={() => setShowAddEmployee(true)}
            className="flex items-center gap-2 px-4 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600 transition-colors"
          >
            <UserPlus size={16} />
            Add Employee
          </button>
        </div>
      </div>

      {/* Statistics Cards */}
      <div className="grid grid-cols-1 md:grid-cols-4 gap-6">
        <div className="bg-white rounded-xl shadow-sm border p-6">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm font-medium text-gray-600">Total Employees</p>
              <p className="text-2xl font-bold text-gray-900">{employees.length}</p>
            </div>
            <div className="w-12 h-12 bg-blue-100 rounded-lg flex items-center justify-center">
              <Users className="text-blue-600" size={24} />
            </div>
          </div>
        </div>

        <div className="bg-white rounded-xl shadow-sm border p-6">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm font-medium text-gray-600">Departments</p>
              <p className="text-2xl font-bold text-green-600">{departments.length}</p>
            </div>
            <div className="w-12 h-12 bg-green-100 rounded-lg flex items-center justify-center">
              <Building className="text-green-600" size={24} />
            </div>
          </div>
        </div>

        <div className="bg-white rounded-xl shadow-sm border p-6">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm font-medium text-gray-600">Active Employees</p>
              <p className="text-2xl font-bold text-purple-600">{employees.filter(e => e.status === 'active').length}</p>
            </div>
            <div className="w-12 h-12 bg-purple-100 rounded-lg flex items-center justify-center">
              <User className="text-purple-600" size={24} />
            </div>
          </div>
        </div>

        <div className="bg-white rounded-xl shadow-sm border p-6">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm font-medium text-gray-600">Managers</p>
              <p className="text-2xl font-bold text-amber-600">{employees.filter(e => e.position.includes('Manager') || e.position === 'CEO' || e.position === 'CTO' || e.position === 'CFO').length}</p>
            </div>
            <div className="w-12 h-12 bg-amber-100 rounded-lg flex items-center justify-center">
              <Crown className="text-amber-600" size={24} />
            </div>
          </div>
        </div>
      </div>

      {/* Search and Filters */}
      <div className="bg-white rounded-xl shadow-sm border">
        <div className="p-6 border-b">
          <div className="flex flex-col lg:flex-row gap-4">
            <div className="flex-1 relative">
              <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400" size={20} />
              <input
                type="text"
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                placeholder="Search employees by name, email, position, or ID..."
                className="w-full pl-10 pr-4 py-3 border rounded-lg focus:outline-none focus:ring-2 focus:ring-amber-500"
              />
            </div>
            <div className="flex gap-3">
              <select
                value={departmentFilter}
                onChange={(e) => setDepartmentFilter(e.target.value)}
                className="px-4 py-3 border rounded-lg focus:outline-none focus:ring-2 focus:ring-amber-500"
              >
                <option value="all">All Departments</option>
                {departments.map(dept => (
                  <option key={dept.id} value={dept.name}>{dept.name}</option>
                ))}
              </select>
              <div className="flex border rounded-lg">
                <button
                  onClick={() => setViewMode('tree')}
                  className={`px-4 py-3 ${viewMode === 'tree' ? 'bg-blue-500 text-white' : 'text-gray-600 hover:bg-gray-50'} rounded-l-lg transition-colors`}
                >
                  Tree
                </button>
                <button
                  onClick={() => setViewMode('list')}
                  className={`px-4 py-3 ${viewMode === 'list' ? 'bg-blue-500 text-white' : 'text-gray-600 hover:bg-gray-50'} rounded-r-lg transition-colors`}
                >
                  List
                </button>
              </div>
            </div>
          </div>
        </div>

        {/* Organization Tree/List */}
        <div className="p-6">
          {viewMode === 'tree' ? (
            <div className="space-y-2">
              {buildHierarchy().map(node => renderTreeNode(node))}
            </div>
          ) : (
            <div className="space-y-3">
              {filteredEmployees.map(employee => (
                <div key={employee.id} className="flex items-center gap-4 p-4 border rounded-lg hover:bg-gray-50">
                  <div className="w-12 h-12 bg-gradient-to-br from-blue-400 to-blue-600 rounded-lg flex items-center justify-center">
                    <span className="text-white font-semibold">{employee.avatar}</span>
                  </div>
                  <div className="flex-1">
                    <div className="flex items-center gap-2">
                      <h3 className="font-medium text-gray-900">{employee.name}</h3>
                      {employee.position === 'CEO' && <Crown size={16} className="text-yellow-500" />}
                      {employee.position.includes('Manager') && <Shield size={16} className="text-blue-500" />}
                    </div>
                    <p className="text-sm text-gray-600">{employee.position} • {employee.department}</p>
                    <p className="text-xs text-gray-500">{employee.email} • {employee.phone}</p>
                  </div>
                  <div className="text-right">
                    <p className="text-sm font-medium text-gray-900">{employee.employeeId}</p>
                    <p className="text-xs text-gray-500">Hired: {new Date(employee.hireDate).toLocaleDateString()}</p>
                  </div>
                  <div className="flex gap-2">
                    <button
                      onClick={() => openEditEmployee(employee)}
                      className="p-2 text-blue-600 hover:bg-blue-50 rounded-lg transition-colors"
                      title="Edit"
                    >
                      <Edit size={16} />
                    </button>
                    <button
                      onClick={() => handleDeleteEmployee(employee.id)}
                      className="p-2 text-red-600 hover:bg-red-50 rounded-lg transition-colors"
                      title="Delete"
                    >
                      <Trash2 size={16} />
                    </button>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      </div>

      {/* Add/Edit Employee Modal */}
      {(showAddEmployee || editingEmployee) && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
          <div className="bg-white rounded-xl shadow-xl max-w-2xl w-full max-h-[90vh] overflow-hidden flex flex-col">
            <div className="p-6 border-b flex-shrink-0">
              <div className="flex justify-between items-center">
                <h3 className="text-xl font-bold">
                  {editingEmployee ? 'Edit Employee' : 'Add New Employee'}
                </h3>
                <button
                  onClick={() => {
                    setShowAddEmployee(false);
                    setEditingEmployee(null);
                    resetEmployeeForm();
                  }}
                  className="text-gray-400 hover:text-gray-600 transition-colors"
                >
                  ✕
                </button>
              </div>
            </div>
            
            <div className="p-6 overflow-y-auto flex-1">
              <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">Full Name *</label>
                  <input
                    type="text"
                    value={employeeForm.name}
                    onChange={(e) => setEmployeeForm(prev => ({ ...prev, name: e.target.value }))}
                    className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-amber-500 focus:border-transparent"
                    placeholder="Enter full name"
                  />
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">Employee ID *</label>
                  <input
                    type="text"
                    value={employeeForm.employeeId}
                    onChange={(e) => setEmployeeForm(prev => ({ ...prev, employeeId: e.target.value }))}
                    className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-amber-500 focus:border-transparent"
                    placeholder="EMP001"
                  />
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">Email *</label>
                  <input
                    type="email"
                    value={employeeForm.email}
                    onChange={(e) => setEmployeeForm(prev => ({ ...prev, email: e.target.value }))}
                    className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-amber-500 focus:border-transparent"
                    placeholder="employee@company.com"
                  />
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">Phone *</label>
                  <input
                    type="tel"
                    value={employeeForm.phone}
                    onChange={(e) => setEmployeeForm(prev => ({ ...prev, phone: e.target.value }))}
                    className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-amber-500 focus:border-transparent"
                    placeholder="+91-98765-43210"
                  />
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">Position *</label>
                  <input
                    type="text"
                    value={employeeForm.position}
                    onChange={(e) => setEmployeeForm(prev => ({ ...prev, position: e.target.value }))}
                    className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-amber-500 focus:border-transparent"
                    placeholder="Software Engineer"
                  />
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">Department *</label>
                  <select
                    value={employeeForm.department}
                    onChange={(e) => setEmployeeForm(prev => ({ ...prev, department: e.target.value }))}
                    className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-amber-500 focus:border-transparent"
                  >
                    <option value="">Select Department</option>
                    {departments.map(dept => (
                      <option key={dept.id} value={dept.name}>{dept.name}</option>
                    ))}
                  </select>
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">Manager</label>
                  <select
                    value={employeeForm.manager}
                    onChange={(e) => setEmployeeForm(prev => ({ ...prev, manager: e.target.value || null }))}
                    className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-amber-500 focus:border-transparent"
                  >
                    <option value="">No Manager</option>
                    {employees.map(emp => (
                      <option key={emp.id} value={emp.id}>{emp.name} - {emp.position}</option>
                    ))}
                  </select>
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">Hire Date *</label>
                  <input
                    type="date"
                    value={employeeForm.hireDate}
                    onChange={(e) => setEmployeeForm(prev => ({ ...prev, hireDate: e.target.value }))}
                    className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-amber-500 focus:border-transparent"
                  />
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">Salary</label>
                  <input
                    type="text"
                    value={employeeForm.salary}
                    onChange={(e) => setEmployeeForm(prev => ({ ...prev, salary: e.target.value }))}
                    className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-amber-500 focus:border-transparent"
                    placeholder="₹8,00,000"
                  />
                </div>
                <div className="md:col-span-2">
                  <label className="block text-sm font-medium text-gray-700 mb-2">Address</label>
                  <textarea
                    value={employeeForm.address}
                    onChange={(e) => setEmployeeForm(prev => ({ ...prev, address: e.target.value }))}
                    rows={3}
                    className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-amber-500 focus:border-transparent"
                    placeholder="Complete address"
                  />
                </div>
                <div className="md:col-span-2">
                  <label className="block text-sm font-medium text-gray-700 mb-2">Emergency Contact</label>
                  <input
                    type="text"
                    value={employeeForm.emergencyContact}
                    onChange={(e) => setEmployeeForm(prev => ({ ...prev, emergencyContact: e.target.value }))}
                    className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-amber-500 focus:border-transparent"
                    placeholder="Contact Name - Phone Number"
                  />
                </div>
              </div>
            </div>

            <div className="p-6 border-t flex-shrink-0">
              <div className="flex gap-3">
                <button
                  onClick={editingEmployee ? handleEditEmployee : handleAddEmployee}
                  className="flex-1 px-4 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600 transition-colors"
                >
                  {editingEmployee ? 'Update Employee' : 'Add Employee'}
                </button>
                <button
                  onClick={() => {
                    setShowAddEmployee(false);
                    setEditingEmployee(null);
                    resetEmployeeForm();
                  }}
                  className="px-4 py-2 border border-gray-300 text-gray-700 rounded-lg hover:bg-gray-50 transition-colors"
                >
                  Cancel
                </button>
              </div>
            </div>
          </div>
        </div>
      )}

      {/* Add/Edit Department Modal */}
      {(showAddDepartment || editingDepartment) && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
          <div className="bg-white rounded-xl shadow-xl max-w-lg w-full max-h-[90vh] overflow-hidden flex flex-col">
            <div className="p-6 border-b flex-shrink-0">
              <div className="flex justify-between items-center">
                <h3 className="text-xl font-bold">
                  {editingDepartment ? 'Edit Department' : 'Add New Department'}
                </h3>
                <button
                  onClick={() => {
                    setShowAddDepartment(false);
                    setEditingDepartment(null);
                    resetDepartmentForm();
                  }}
                  className="text-gray-400 hover:text-gray-600 transition-colors"
                >
                  ✕
                </button>
              </div>
            </div>
            
            <div className="p-6 overflow-y-auto flex-1">
              <div className="space-y-4">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">Department Name *</label>
                  <input
                    type="text"
                    value={departmentForm.name}
                    onChange={(e) => setDepartmentForm(prev => ({ ...prev, name: e.target.value }))}
                    className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-amber-500 focus:border-transparent"
                    placeholder="Technology"
                  />
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">Description</label>
                  <textarea
                    value={departmentForm.description}
                    onChange={(e) => setDepartmentForm(prev => ({ ...prev, description: e.target.value }))}
                    rows={3}
                    className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-amber-500 focus:border-transparent"
                    placeholder="Department description"
                  />
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">Department Head</label>
                  <select
                    value={departmentForm.head}
                    onChange={(e) => setDepartmentForm(prev => ({ ...prev, head: e.target.value || null }))}
                    className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-amber-500 focus:border-transparent"
                  >
                    <option value="">Select Department Head</option>
                    {employees.map(emp => (
                      <option key={emp.id} value={emp.id}>{emp.name} - {emp.position}</option>
                    ))}
                  </select>
                </div>
                <div className="grid grid-cols-2 gap-4">
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-2">Budget</label>
                    <input
                      type="text"
                      value={departmentForm.budget}
                      onChange={(e) => setDepartmentForm(prev => ({ ...prev, budget: e.target.value }))}
                      className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-amber-500 focus:border-transparent"
                      placeholder="₹30,00,000"
                    />
                  </div>
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-2">Location</label>
                    <input
                      type="text"
                      value={departmentForm.location}
                      onChange={(e) => setDepartmentForm(prev => ({ ...prev, location: e.target.value }))}
                      className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-amber-500 focus:border-transparent"
                      placeholder="Bangalore"
                    />
                  </div>
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">Established Date</label>
                  <input
                    type="date"
                    value={departmentForm.establishedDate}
                    onChange={(e) => setDepartmentForm(prev => ({ ...prev, establishedDate: e.target.value }))}
                    className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-amber-500 focus:border-transparent"
                  />
                </div>
              </div>
            </div>

            <div className="p-6 border-t flex-shrink-0">
              <div className="flex gap-3">
                <button
                  onClick={handleAddDepartment}
                  className="flex-1 px-4 py-2 bg-green-500 text-white rounded-lg hover:bg-green-600 transition-colors"
                >
                  {editingDepartment ? 'Update Department' : 'Add Department'}
                </button>
                <button
                  onClick={() => {
                    setShowAddDepartment(false);
                    setEditingDepartment(null);
                    resetDepartmentForm();
                  }}
                  className="px-4 py-2 border border-gray-300 text-gray-700 rounded-lg hover:bg-gray-50 transition-colors"
                >
                  Cancel
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default OrganizationStructureView;
